const contactFormSubmit=document.getElementById("contact-form-submit");contactFormSubmit.addEventListener("click",validateForm);function validateForm(d){d.preventDefault();d.stopPropagation();const f=document.getElementById("full_name")!==null?document.getElementById("full_name").value:"";const a=document.getElementById("email")!==null?document.getElementById("email").value:"";const c=document.getElementById("message")!==null?document.getElementById("message").value:"";const e=[];if(f.length===0){e.push("Please enter a valid name.")}if(a.length===0||!emailIsValid(a)){e.push("Please enter a valid email address.")}if(c.length===0){e.push("Please enter a valid message!")}if(e.length===0){document.getElementById("contact_form").submit()}else{const b=document.getElementById("validation-messages-container");while(b.firstChild){b.removeChild(b.firstChild)}e.forEach(function(j,h){const g=document.createElement("div");g.classList.add("validation-message");const k=document.createTextNode(j);g.appendChild(k);const i=document.getElementById("validation-messages-container");i.appendChild(g)})}}function emailIsValid(a){const b=/\S+@\S+\.\S+/;return b.test(a)};